INSERT INTO `think_animate` (`id`, `name`, `content`, `vactor`, `pic`, `time`, `company`, `tid`, `click`, `rate`, `reply`, `state`) VALUES
(1, '甘城光辉游乐园', '被谜之转校生——千斗五十铃强迫而接受游乐园约会的可儿江西也。不知何故被拉到的地方，却是一个充满了破旧游乐设施的日本第一的遗憾游乐园——甘城辉煌游乐园。\r\n被引荐给了游乐园的管理者，“真正的”公主——拉媞珐的西也，不知为何突然被托付了重建这个陷入闭园危机的游乐园的重任。原来，这里不是游乐园——？！', '', '/Tp_group/Uploads/201412/5486e84ac6dfc.JPG', 1418178042, '京都', 2, 0, 10, 0, 0),
(2, '魔弹之王与战姬', '布琉努王国里有块位于边境的弹丸之地亚尔萨斯，而这里的领导人是个随遇而安的贵族少年堤格尔维尔穆德·冯伦。因为王国的征招，堤格尔被迫上到战场上，双方人数相差快5倍，原本以为这场是必胜的战争，然而对手是吉斯塔特中的“七战姬”之一、拥有“银闪的风姬”名号的艾蕾欧诺拉·维尔塔利亚，在她出奇不意的战略下，击破了布琉努大军。战乱中堤格尔虽然尝试利用最擅长的弓击杀敌人主将，但却反而被她高超的剑技打败。就在堤格尔绝望的做好受死准备之时……', '', '/Tp_group/Uploads/201410/544e284e61074.jpg', 1418177715, 'SATELIGHT', 2, 0, 10, 0, 0),
(3, 'Fate/stay night-UBW', '圣杯是传说中可实现持有者一切愿望的宝物。而为了得到圣杯的仪式就被称为圣杯战争。 \r\n　　参加圣杯战争的7名由圣杯选出的魔术师被称为Master，与7名被称为Servant的使魔订定契约。他们是由圣杯选择的七位英灵，被分为七个职阶，以使魔的身份被召唤出来。能获得圣杯的只有一组，这7组人马各自为了成为最后的那一组而互相残杀。 ', '', '/Tp_group/Uploads/201412/5486e99b0719b.jpg', 1418177553, 'ufotable', 2, 0, 10, 0, 0),
(4, '大图书馆的牧羊人', '男主角笕京太郎是位具有能预测马上会发生的事件能力的高中生，作为图书社的成员过着随波逐流的人生。但是因为有樱庭玉藻、御园千莉、铃木佳奈、高峰一景，再加上不知为何经常来凑热闹的小太刀凪，本来是最棒的读书空间的社团室变成了午休时间的教室一般喧闹。故事便围绕着这样一群人的日常展开，不过在某些特定的关键时刻，大家会收到以牧羊人之名义发送的短信。男主小时候曾收到编号第771号牧羊人的书签，内容是牧羊人的人生纪录。那么牧羊人究竟是谁呢？', '', '/Tp_group/Uploads/201412/5486ea87c343b.jpg', 1418177573, 'Hoods Entertainment', 2, 0, 10, 0, 0),
(5, '四月是你的谎言', '有马公生的母亲一心想把有马培育成举世闻名的钢琴家，而有马也不负母亲的期望，在念小学时就赢得许多钢琴比赛的大奖。11岁的秋天，有马的母亲过世，从此他再也听不见自己弹奏的钢琴声，沮丧的他也只好放弃演奏，但在14岁那年，经由儿时玩伴的介绍，有马认识了小提琴手宫园薰，并被薰的自由奔放吸引，没想到薰竟开口邀请公生在比赛时担任她的伴奏…', '', '/Tp_group/Uploads/201410/544e35f3a6128.jpg', 1418177593, 'A-1 Pictures', 2, 0, 10, 0, 0),
(6, '舰队Collection', '电视动画以驱逐舰吹雪为主人公，以她的视点展开故事。但是由于物理上的因素，原作游戏中的所有舰娘无法悉数登场，最终能参演动画的主要角色只有数十位，而这些舰娘的声优阵容大抵将沿用原游戏中的人选', '', '/Tp_group/Uploads/201412/5486ec98cbd13.jpg', 1418177604, 'Diomedéa', 6, 0, 10, 0, 0),
(7, 'lovelive！', '在秋叶原，神田和神保町这三条街上的传统学校，音乃木坂学园正面临着废校的大危机。\r\n面对学校的危机，以二年生高坂穗乃果为中心的九位女生站了出来。', '', '/Tp_group/Uploads/201412/5486ed9cc0de8.jpg', 1418177615, 'SUNRISE', 1, 0, 10, 0, 0),
(8, 'TRINITY SEVEN', '主要讲述了主人公春日新的日常生活，随着名为“崩坏现象”的奇妙事件的发生，与表妹春日圣一同消失在了异界之中。为了拯救因“崩坏现象”而消失的表妹，新进入了王立比布利亚学园就读。然而意想不到的是，在那里等待他的是巨乳、双马尾、戴眼镜等各具魅力的七名美少女魔道士“TRINITY SEVEN”。就这样，有时严肃的魔法学园爱情喜剧正式拉开帷幕。', '', '/Tp_group/Uploads/201412/5486f1004be9e.jpg', 1418177627, 'SEVEN ARCS PICTURES', 2, 0, 10, 0, 0),
(9, 'FripSide', 'FripSide', '南条爱乃', '/Tp_group/Uploads/201412/5486f21659be7.jpg', 1418177646, '', 1, 0, 10, 0, 0),
(10, 'Sword Art Online', 'SAO', '', '/Tp_group/Uploads/201412/5486f2ba5aad2.jpg', 1418178076, 'A-1 Pictures', 1, 0, 10, 0, 0),
(11, '甘城光辉游乐园', '甘城光辉游乐园', '', '/Tp_group/Uploads/201412/5486f361851c1.png', 1418177683, '', 1, 0, 10, 0, 0),
(12, '斩·赤红之瞳', '故事讲述来自因帝国镇压而痛苦不堪的村子的少年剑士塔兹米和幼驯染的伙伴一起出发前往帝都谋生，在抵达帝都的途中遇到歹徒夜袭，与伙伴分离孤单一人到达了帝都。在到达帝都的第一天就被骗光了所有钱的塔兹米，被贵族的大小姐艾莉亚收留回家。在一次与艾莉亚一起去买东西的途中，意外地得知了帝都黑暗的真相。此后塔兹米与帝都的无情杀手集团NIGHT RAID的赤瞳相遇，决定以NIGHT RAID一员的身份对抗帝都的所有恶势力。', '雨宫天', '/Tp_group/Uploads/201412/5486f3e5d7e4b.jpg', 1418177737, 'WHITE FOX', 2, 0, 10, 0, 0),
(13, '铳皇无尽的法夫纳', '故事描述了地球突然出现总称为“龙”的怪物，从此世界为之一变，终于人类之中也诞生拥有龙之力，并称之为“D”的异能少女们。某一天，世界唯一的男性“D”物部悠，带著一张调职令，来到聚集了全世界的“D”的学园米德加尔。在那里，他目睹了银发少女伊莉丝·弗蕾亚的裸体，又与三年前分离的妹妹物部深月再会，就此展开了全新的学生生活。', '', '/Tp_group/Uploads/201412/5486f5001c9db.jpg', 1418177752, 'diomedéa', 6, 0, 10, 0, 0),
(14, '天使与龙的轮舞', '故事讲述了掌握了高端情报技术“玛娜”的人类，借助它的力量克服了地球上的战争、饥饿、污染等问题，终于迎来了和平的理想乡。然而，作为米斯尔奇王国第一公主的安琪莉洁，却因无法使用这等魔法之力而沦为士兵，并被派遣到偏僻的岛屿上，同其他少女们操纵变形人型兵器“帕拉美露”，共同对抗入侵而至的巨大攻击型生物——龙。于是，少女的不屈物语就此开始了……', '', '/Tp_group/Uploads/201412/5486ff8eb0f92.jpg', 1418177788, 'SUNRISE', 6, 0, 10, 0, 0),
(15, 'Fate/stay night-UBW', 'Fate/stay night-UBW', '', '/Tp_group/Uploads/201412/5486f6df25168.jpg', 1418178385, '', 1, 0, 10, 0, 0),
(16, '东京喰种', '在纷乱嘈杂的现代化城市——东京，蔓延着一种吞食死尸的怪人，人们称之为喰种。那一日，金木研——上井大学的一名普通学生——遇上了某位神秘女子（实为喰种），进而卷入了一场精心策划的事故。自此，充满波折的命运齿轮开始转动了', '', '/Tp_group/Uploads/201412/5486f75c801b8.jpg', 1418178157, 'studio Pierrot', 1, 0, 10, 0, 0),
(17, '大图书馆的牧羊人', '男主角笕京太郎是位具有能预测马上会发生的事件能力的高中生，作为图书社的成员过着随波逐流的人生。但是因为有樱庭玉藻、御园千莉、铃木佳奈、高峰一景，再加上不知为何经常来凑热闹的小太刀凪，本来是最棒的读书空间的社团室变成了午休时间的教室一般喧闹。故事便围绕着这样一群人的日常展开，不过在某些特定的关键时刻，大家会收到以牧羊人之名义发送的短信。男主小时候曾收到编号第771号牧羊人的书签，内容是牧羊人的人生纪录。那么牧羊人究竟是谁呢？', '米泽圆', '/Tp_group/Uploads/201410/54476f9d01076.jpg', 1418178207, 'Hoods Entertainment', 6, 0, 9, 0, 0),
(18, '关于完全听不懂老公在说什么的事', '有个宅男老公也许也是一件挺有趣的事吧？热心于工作的OL“薰”是个普通人，而她的老公这是沉迷于某大型网路留言版的阿宅，故事就围绕着这样的2人的夫妻日常展开', '', '/Tp_group/Uploads/201410/5447705b35bbe.jpg', 1418178324, 'SEVEN', 2, 0, 8, 0, 0),
(19, '黑子的篮球第二季', '日本人气动画《黑子的篮球》的续作，黑子的篮球全集动画改编自改编自藤卷忠俊创作的同名漫画，讲述的是帝光中学中学篮球部，部员一百余人全国联赛三年冕的超级豪门，在其光辉历史中有五个十年难遇的天才，他们被称为“奇迹的世代”，但有一个奇妙的传说，谁都未知晓也没有上场记录，即使如此，仍让五个天才也不敢小看的一个人，他就是幻之第六人，本作的男主角黑子哲也', '小野贤章，小野友树', '/Tp_group/Uploads/201410/544770e2d2e6c.jpg', 1418178347, 'Production I.G', 6, 0, 8, 0, 0),
(20, '晨曦公主', '从小备受父王及哈克宝贝呵护的高华国公主‧优娜，在她16岁生日的当天，她的心上人蘇芳来到宫殿，并送她漂亮的发簪。不过优娜的父亲并不允许她跟蘇芳的交往，依旧对蘇芳无法死心的优娜决定向父王表明她的心意时，却意外地撞见父亲被刺杀的惊人场面。不肯面对事实的优娜从此便与护卫哈克展开一连串的逃亡生活', '斋藤千和，前野智昭，小林裕介', '/Tp_group/Uploads/201410/544772761b915.jpg', 1414058384, 'Studio Pierrot', 6, 0, 10, 0, 0),
(21, '棺姬嘉依卡', '托鲁一行在航天要塞事件结束後，旅程还要继续下去。一行人揣著未解的无数谜团，前去皇帝遗体所沉眠的海域。\r\n　　他们和红色嘉依卡意外重逢，并突然惨遭来历不明的亚人士兵及大海魔的猛烈攻击！激战过後，他们漂流到一座远海上的孤岛。托鲁一行人在岛上的牢狱之中，遇上了一位阴阳妖瞳的少女。', '安济知佳，间岛淳司，原优子，斋藤千和', '/Tp_group/Uploads/201410/544e32405f78b.jpg', 1416968561, 'BONES', 2, 0, 10, 0, 0),
(22, '寄生兽生命的准则', '某天于地球上空出现许多孢子，其中诞生的幼虫侵入人体后，可以吞噬掉宿主的头部取而代之，从而管理这个“死掉的身体”，它们能任意变形，其食物便是与寄生体相同的物种。男主角泉新一就是被寄生生物寄生的人类之一，但是由于某种意外，寄生在他体内的生物并没有吃掉他的大脑，而只是取代了他的右手。本性善良正直的泉新一没有被这突如其来的残酷命运击倒，为了被寄生兽杀死的亲友，以及所有人类的未来，他决心与其他完全体寄生兽进行殊死搏斗', '', '/Tp_group/Uploads/201410/544775af1aa30.jpg', 1418178402, 'MADHOUSE', 6, 0, 10, 0, 0),
(23, '我，要成为双马尾', '某天，当来自异世界的美少女‧朵艾儿现身在他面前的同时，总二居住的城镇竟冒出许多怪物！', '内田真礼，上坂堇', '/Tp_group/Uploads/201410/544e327d77079.jpg', 1414410877, 'Production IMS', 6, 0, 10, 0, 0),
(24, '结城友奈是勇者', '故事发生在神世纪300年，主要讲述结城友奈是初中2年级学生，她天天在学校里过着与很多朋友一同听课、进行社团活动、玩耍的平稳生活。不管怎么看这都是非常普通的女孩的生活日程，但是只有一点与其他孩子决定性不同的地方，那就是她所属的名为“勇者部”的社团活动。以部长犬吠埼风为中心的该社团的活动内容与被谜团包围的叫做Vertex的不可思议的存在有关系，本作正是以描写友奈等人的“勇者部”的活动为主', '内山夕实，黑泽知叶', '/Tp_group/Uploads/201410/5447770f2f6c2.jpg', 1418178372, 'Studio 五组', 2, 0, 10, 0, 0),
(25, '加速世界', '少年，想加速到更快的境界么？——BlackLotus', '', '/Tp_group/Uploads/201412/5486f86439a36.jpg', 1418131556, '', 1, 0, 10, 0, 3),
(26, '三坪房间的侵略者', '以高中入学为契机为开始一个人生活的里见孝太郎，幸运的他找到了只要三坪只要五千日圆的便宜房间可乐娜庄的106号房，而他也以此展开了全新的生活。只不过刚搬进房间的他居然被抢先幽灵抢先入住！甚至地底人、宇宙人、魔法少女也出来抢这间房间！主张有着所有权的孝太郎更是一步也无所退让！以三坪大小为战场，侵略者们将展开行动', '中村悠一，悠木碧，竹达彩奈，铃木絵理', '/Tp_group/Uploads/201410/5448eacf10e42.jpg', 1414064847, 'SILVER LINK.', 3, 0, 9, 0, 0),
(27, '临时女友', '内容是一款专门收集校园女孩的卡牌RPG游戏。玩家是圣樱学园高中二年级的男学生，在上学时有机会可以认识女孩子，每张卡片的等级，属性都不相同，玩家需透过战斗，强化提升卡片的能力素质。卡片依稀有度分成N，HN，R，HR，SR，SSR，UR七个等级。N，HN的卡片容易取得但素质普通，R级以上的卡片需要透过游戏中活动来取得，每张卡片都有声优的配音，随著卡片等级的不同，女生的说话内容也有所变动，当卡片等级最高，好感度全满时，也有机会出现额外的隐藏配音。', '略', '/Tp_group/Uploads/201410/544e2d4a10358.jpg', 1414409546, '略', 4, 0, 7, 0, 0),
(28, '日常系的异能战斗', '以总是憧憬能去使用异能力战斗的世界的高中生安藤寿为原点,没想到某天真正的超能力在他身上觉醒,于是他便有机会成为了学园异能战斗的主角.——虽然可以这么想,但生活在和平的每一天里,既没有毁灭世界的秘密机关,异能战争之类的设定,也没有勇者,魔王等角色的存在.当包括主角在内的 文艺部全员都成为了最强异能持有者,围绕着他们荒谬绝伦的超能力的故事就此上演.', '略', '/Tp_group/Uploads/201410/5448ec1367f0a.jpg', 1414065171, '略', 6, 0, 10, 0, 0),
(29, '火影忍者', '火影忍者', '', '/Tp_group/Uploads/201412/5486fdb74b476.jpeg', 1418132919, '', 1, 0, 8, 0, 0),
(30, '东京ESP', '“在我遇见他之前，我是不相信（奇迹）那种东西存在的。”带着这样的想法，漆叶凛华在回家路上发现了“飞在空中的企鹅”为了一夜暴富的凛华追踪企鹅的时候遇到了发光的鱼群，在追寻“飞在空中的企鹅”时，与空中飞过的光之鱼接触后失去了意识。第二天醒来，身体竟然穿过了地板！？同时东京出现了大量的超能力觉醒者', '木户衣吹，河本启佑', '/Tp_group/Uploads/201410/5448eef6e5804.jpg', 1414065910, 'XEBEC', 6, 0, 10, 0, 0),
(31, '日常系的异能战斗', '以总是憧憬能去使用异能力战斗的世界的高中生安藤寿来为原点，没想到某天真正的超能力在他身上觉醒，于是他便有机会成为了学园异能战斗的主角。——虽然可以这么想，但生活在和平的每一天里，既没有毁灭世界的秘密机关、异能战争之类的设定，也没有勇者、魔王等角色的存在。当包括主角在内的 文艺部全员都成为了最强异能持有者，围绕着他们荒谬绝伦的超能力的故事就此上演。', '', '/Tp_group/Uploads/201412/5486fe782e757.png', 1418133112, 'TRIGGER', 2, 0, 10, 0, 0),
(32, '南条爱乃', 'Nanjo Yoshino', '', '/Tp_group/Uploads/201412/548708be1bd86.jpg', 1418135742, '', 1, 0, 0, 0, 0);
