INSERT INTO `think_blog` (`id`, `title`, `content`, `pic`, `summary`, `time`, `cid`, `del`, `click`) VALUES
(50, '【CV】南条爱乃', '<blockquote>\r\n	<span style="font-size:32px;color:#E53333;"><strong>南条爱乃</strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:24px;color:#337FE5;"><strong>【人物简介】</strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:24px;color:#337FE5;"><strong><img src="/Tp_group/Uploads/201412/54855296dd764.jpg" alt="" /><br />\r\n</strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:24px;color:#337FE5;"><strong> \r\n	<table style="width:800px;" cellpadding="2" cellspacing="0" border="1" bordercolor="#000000">\r\n		<tbody>\r\n			<tr>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">中文名</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">南条爱乃</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">外文名</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-family:arial, 宋体, sans-serif;line-height:26px;background-color:#FFFFFF;font-size:14px;">南條 愛乃（なんじょう よしの，Nanjō Yoshino）</span><br />\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">出生日期</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">1984年7月12日</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">职业</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-family:arial, 宋体, sans-serif;line-height:26px;background-color:#FFFFFF;font-size:14px;">声优、歌手</span><br />\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">国籍</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">日本</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">出道时间</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">2006</span> \r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">经纪公司</span><br />\r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-family:arial, 宋体, sans-serif;line-height:26px;background-color:#FFFFFF;font-size:14px;">Amuleto（声优事务所）、NBC Universal（唱片公司）</span><span style="color:#000000;font-size:14px;"></span><br />\r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">所属团体</span> \r\n				</td>\r\n				<td>\r\n					<span style="color:#000000;font-size:14px;">fripSide</span><span style="color:#000000;font-family:arial, 宋体, sans-serif;line-height:26px;background-color:#FFFFFF;font-size:14px;">、μ‘s</span><span style="color:#000000;font-size:14px;"></span><br />\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n<br />\r\n【人物经历】</strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:24px;color:#337FE5;"><strong><span style="color:#000000;font-size:14px;">代代木动画学院</span><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">、RAMS Professional Education毕业。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2007年10月1日退出RAMS，11月1日加入Dwango Artist Production，</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:24px;color:#337FE5;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2009年8月7日，成为乐团</span><span style="color:#000000;font-size:14px;">fripSide</span><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">的第二代主唱，并为动画《</span><span style="color:#000000;font-size:14px;">科学超电磁炮</span><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">》献唱OP“</span><span style="color:#000000;font-size:14px;">o</span><span style="color:#000000;font-size:14px;">nly my railgun</span><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">”，单曲CD于2009年11月4日发售。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2010年8月28日，以fripSide的身份第一次登上“Animelo Summer Live”的舞台。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2011年2月14日，出席《REBOKON BULE 家庭教师声优演唱会》。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2011年4月1日，转入同属MAGES.旗下的声优事务所Amuleto。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2011年4月7日作为嘉宾出席《电波研究社》并进行动画《圣痕炼金士Ⅱ》的宣传。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2011年8月27日，以fripSide的身份第二次登上“Animelo Summer Live”的舞台。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2012年8月26日，以μ''s绚濑绘里的身份第三次登上“Animelo Summer Live”的舞台。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2012年11月10日，以fripSide身份参加“新加坡ANIME FESTIVAL ASIA 2012”演出。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2012年11月14日，预定发售首张个人迷你专辑『カタルモア』，并会以个人名义出道，随后专辑发售日延迟至2012年12月12日。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2013年8月23日，以μ''s绚濑绘里的身份第四次登上“Animelo Summer Live”的舞台。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2013年8月24日，以fripSide的身份第五次登上“Animelo Summer Live”的舞台。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:14px;color:#000000;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2013年11月23日，以fripside的身份参加“ANIMAX MUSIX 2013”的演出。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:24px;color:#337FE5;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">2014年1月18日，以fripside身份前往中国</span><span style="color:#000000;font-size:14px;">台湾</span><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;">参加“ANIMAX MUSIX TAIWAN 2013”的演出。</span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:24px;color:#337FE5;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><span style="color:#337FE5;font-size:24px;">【出演作品Animation</span><span style="color:#337FE5;font-size:24px;">】</span></span></strong></span> \r\n</blockquote>\r\n<blockquote>\r\n	<span style="font-size:24px;color:#337FE5;"><strong><span style="color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;"><span style="color:#337FE5;font-size:24px;"> \r\n	<table class="table-view log-set-param" style="margin:5px 0px;font-size:12px;color:#000000;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;">\r\n		<tbody>\r\n			<tr>\r\n				<th style="border:1px solid #E6E6E6;text-align:center;background-color:#F9F9F9;">\r\n					<div class="para" style="color:#333333;margin:0px;">\r\n						年份\r\n					</div>\r\n				</th>\r\n				<th style="border:1px solid #E6E6E6;text-align:center;background-color:#F9F9F9;">\r\n					<div class="para" style="color:#333333;margin:0px;">\r\n						作品/角色\r\n					</div>\r\n				</th>\r\n			</tr>\r\n			<tr>\r\n				<td width="58" height="0" align="center" valign="middle" rowspan="3" style="border:1px solid #E6E6E6;">\r\n					<div class="para" style="color:#333333;margin:0px;">\r\n						<b>2006年</b> \r\n					</div>\r\n				</td>\r\n				<td width="585" align="left" valign="center" style="border:1px solid #E6E6E6;">\r\n					<div class="para" style="color:#333333;margin:0px;">\r\n						Soul Link（杉本亚弥）\r\n					</div>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="58" align="left" valign="center" style="border:1px solid #E6E6E6;">\r\n					<div class="para" style="color:#333333;margin:0px;">\r\n						娇蛮之吻（近卫穗乃香）\r\n					</div>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width="58" align="left" valign="center" style="border:1px solid #E6E6E6;">\r\n					<div class="para" style="color:#333333;margin:0px;">\r\n						叶之香（美香・如月）\r\n					</div>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n	<table class="table-view log-set-param" style="margin:5px 